-- Hall of Death table for tracking permanent deaths
CREATE TABLE IF NOT EXISTS `hall_of_death` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `player_name` varchar(35) NOT NULL,
  `player_id` int(11) NOT NULL,
  `killer_name` varchar(100) NOT NULL,
  `killer_type` varchar(50) NOT NULL DEFAULT 'Monster',
  `player_level` int(11) NOT NULL DEFAULT 1,
  `player_class` varchar(50) NOT NULL DEFAULT 'Unknown',
  `death_time` bigint(20) NOT NULL,
  `death_location` varchar(100) NOT NULL DEFAULT 'Unknown',
  PRIMARY KEY (`id`),
  KEY `idx_death_time` (`death_time`),
  KEY `idx_player_name` (`player_name`),
  KEY `idx_player_level` (`player_level`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

